<script setup>
import { RouterLink, RouterView } from "vue-router";
import { useRouter } from "vue-router";
import useUserStore from "./store/user";
import AppProvider from "@/components/AppProvider/index.vue";
import { NLayout, NLayoutFooter, NLayoutContent, NLayoutSider } from "naive-ui";
import MyMessage from "./components/MyMessage.vue";
import imgSrc from "./assets/beaver.svg";
import { Icon } from "@vicons/utils";
import { LogInOutline } from "@vicons/ionicons5";
import footerSrc from "./assets/beaver.jpg";
// Import necessary dependencies
const router = useRouter();
const userStore = useUserStore();
// Function to handle user logout
const onLogout = () => {
  // Remove the user's token (authentication token)
  userStore.removeToken();
  // Remove the user's data from the store
  userStore.removeUser();
  router.push("/");
};
// Function to navigate to the home page
const goHome = () => {
  router.push("/myjobs");
};
</script>

