            :src="imgSrc"
            @click="goHome"
            style="cursor: pointer"
          />
          <h1>LET'S SNIFF SOME JOBS!</h1>
        </div>
        <div class="operation">
          <template v-if="userStore.token">
            <n-button
              text
              icon-placement="right"
              color="#be7e4a"
              @click="onLogout"
            >
              <template #icon>
                <!-- <n-icon> <LogInOutline size="48" />Logout </n-icon> -->
                <Icon color="var(--color-text)">
                  <LogInOutline />
                </Icon>
              </template>
              Logout
            </n-button>
          </template>
          <template v-else>
            <div class="operation">
              <router-link class="signLink" to="/signin"> Sign In </router-link>
              <router-link class="signLink" to="/signup"> Sign Up </router-link>
            </div>
          </template>
        </div>
      </div>
      <n-layout>
        <n-layout-content style="padding: 24px">
